package com.krokochik.ideasForum.model;

import lombok.*;
import org.jetbrains.annotations.NotNull;

import javax.persistence.*;
import java.util.Set;

@Data
@RequiredArgsConstructor
@NoArgsConstructor
@ToString(exclude = {"avatar"})
@Entity
@Table(name = "usr")
public class User
{
    @Id
    @GeneratedValue(strategy=GenerationType.AUTO)
    Long id;


    @NotNull
    String username;
    @NotNull
    String nickname;
    @NotNull
    String email;
    @NotNull
    String password;
    byte[] avatar = "data:image/png;base64,".getBytes();
    boolean active = true;
    String mailConfirmationToken;
    String passwordAbortToken;
    boolean confirmMailSent = false;
    boolean passwordAbortSent = false;

    @ElementCollection(targetClass = Role.class, fetch = FetchType.EAGER)
    @CollectionTable(name = "user_role", joinColumns = @JoinColumn(name = "user_id"))
    @Enumerated(EnumType.STRING)
    private Set<Role> roles;

    public User(@NotNull @NonNull String username, @NotNull @NonNull String email, @NotNull @NonNull String password) {
        this.username = username;
        this.email = email;
        this.password = password;
        nickname = username;
    }
}
